import React, { useState } from 'react';

// Components
import Input from '../UI/Input';

// CSS
import styles from './Search.module.scss';

const Search = () => {
    const [searchFocus, setSearchFocus] = useState(false);

    const searchFocused = () => {
        let isFocus = !searchFocus;
        setSearchFocus(isFocus);
    };

    let backdropClasses = styles['search-backdrop'];
    let searchResultClasses = styles['search-result'];
    if (searchFocus) {
        backdropClasses.push('animated');
        searchResultClasses.push('animated');
    }

    return (
        <div className={styles.search}>
            <div
                className={`
                    ${styles['search-backdrop']}
                    ${searchFocus ?
                    styles['animated'] :
                    ''}
                `}>
            </div>
            <Input
                onFocus={searchFocused}
                onBlur={searchFocused}
                placeholder="Search Data"
                hasanimation="true" />
            <div className={searchResultClasses}>
                search
            </div>
        </div>
    )
};

export default Search;
