import React, { useEffect, useState } from 'react';

// Components
import Search from '../components/Search';

// CSS
import styles from './index.module.scss';

const App = () => {
    const [allData, setAllData] = useState([]);

    useEffect(() => {
        const URL = 'http://localhost:3000/api';
        const p1 = new Promise((resolve, reject) => {
            fetch(URL + '/conciliaciones/')
                .then(response => {
                    return response.json();
                })
                .then(data => {
                    resolve({type: 'conciliaciones', ...data});
                });
        });
        const p2 = new Promise((resolve, reject) => {
            fetch(URL + '/fuentes/')
                .then(response => {
                    return response.json();
                })
                .then(data => {
                    resolve({type: 'fuentes', ...data})
                });
        });
        const p3 = new Promise((resolve, reject) => {
            fetch(URL + '/tableros/')
                .then(response => {
                    return response.json();
                })
                .then(data => {
                    resolve({type: 'tableros', ...data});
                });
        });
        const p4 = new Promise((resolve, reject) => {
            fetch(URL + '/usuarios/')
                .then(response => {
                    return response.json();
                })
                .then(data => {
                    resolve({type: 'usuarios', ...data});
                });
        });
        const promises = Promise.all([p1, p2, p3, p4]);

        promises.then(result => {
            let filtered = [];
            let conFiltered = [];
            const filteredUsers = result.forEach(u => {
                if (u.type === 'conciliaciones') {
                    u.data.forEach((user, i) => {
                        if (user.description.includes('sit') || user.conciliationName.includes('sit')) {
                            console.log('in')
                            conFiltered.push(user);
                        }
                    });
                }
            })
            filtered = [{type: 'conciliaciones', data: [...conFiltered]}];
            console.log('filtered', filtered)
            // console.log('filter', conFiltered)
            console.log('result', result)
        })
        .catch(err => console.log(err));
    });

    return (
        <div className={styles.container}>
            <section className={styles.content}>
                <Search />
            </section>
        </div>
    );
};

export default App;
