import React, { useEffect, useRef, useState } from 'react';

// Components
import Search from '../components/Search';

// CSS
import styles from './index.module.scss';

const initialData = {
    usuariosResult: [],
    tablerosResult: [],
    fuentesResult: [],
    conciliacionesResult: [],
    result: []
};

const filterData = (type, data) => {
    result.forEach(item => {
        if (item.type === 'conciliaciones') {
            item.data.forEach((infoItem, i) => {
                if (infoItem.description.includes('adipisicing') || infoItem.conciliationName.includes('adipisicing')) {
                    initialData.conciliacionesResult.push(infoItem);
                }
            });
        }
    });
    initialData.result = [{type: 'conciliaciones', data: [...initialData.conciliacionesResult]}];
    console.log('filtered', initialData.result)
};

const getPromise = (url, type) => {
    return new Promise((resolve, reject) => {
        fetch(url)
            .then(response => {
                return response.json();
            })
            .then(data => {
                resolve({type, ...data});
            });
    });
};

const App = () => {
    const [allData, setAllData] = useState([]);
    const [search, setSearch] = useState('');
    const [searchInput] = useRef(null);

    useEffect(() => {
        const URL = 'http://localhost:3000/api';

        const p1 = getPromise(URL + '/conciliaciones/', 'conciliaciones');
        const p2 = getPromise(URL + '/fuentes/', 'fuentes');
        const p3 = getPromise(URL + '/tableros/', 'tableros');
        const p4 = getPromise(URL + '/usuarios/', 'usuarios');

        const promises = Promise.all([p1, p2, p3, p4]);

        promises.then(result => {
            setAllData(result);
            console.log('result', result)
        })
        .catch(err => console.log(err));
    }, []);

    const handlerSearch = () => {
        setSearch(searchInput.current.value);
    }

    return (
        <div className={styles.container}>
            <section className={styles.content}>
                <Search
                    search={search}
                    searchInput={searchInput}
                    handlerSearch={handlerSearch} />
                {allData.map((data, i) => {
                    return <p key={i}>{data.type}</p>
                })}
            </section>
        </div>
    );
};

export default App;
