import React, { useEffect, useRef, useState } from 'react';

// Components
import Search from '../components/Search';

// CSS
import styles from './index.module.scss';

const initialData = {
    usuariosResult: [],
    tablerosResult: [],
    fuentesResult: [],
    conciliacionesResult: [],
    result: []
};

const getPromise = (url, type) => {
    return new Promise((resolve, reject) => {
        fetch(url)
            .then(response => {
                return response.json();
            })
            .then(data => {
                resolve({type, ...data});
            });
    });
};

const App = () => {
    const [allData, setAllData] = useState([]);
    const [search, setSearch] = useState('');
    const [searchResult, setSearchResult] = useState([]);
    const [searchFocus, setSearchFocus] = useState(false);

    useEffect(() => {
        const URL = 'http://localhost:3000/api';

        const p1 = getPromise(URL + '/conciliaciones/', 'conciliaciones');
        const p2 = getPromise(URL + '/fuentes/', 'fuentes');
        const p3 = getPromise(URL + '/tableros/', 'tableros');
        const p4 = getPromise(URL + '/usuarios/', 'usuarios');

        const promises = Promise.all([p1, p2, p3, p4]);

        promises.then(result => {
            setAllData(result);
            console.log('result', result)
        })
        .catch(err => console.log(err));
    }, []);

    const filterData = (type, searchInput) => {
        initialData.conciliacionesResult = [];
        allData.forEach(item => {
            if (item.type === 'conciliaciones') {
                item.data.forEach((infoItem, i) => {
                    if (infoItem.description.includes(searchInput.toLowerCase())) {
                        initialData.conciliacionesResult.push(infoItem);
                    }
                });
            }
        });
        initialData.result = [{type: 'conciliaciones', data: [...initialData.conciliacionesResult]}];
        setSearchResult(initialData.result);
        console.log('result', searchResult);
        console.log('filtered', initialData.result)
    };

    const handlerSearch = (e) => {
        setSearch(e.target.value);
        filterData(null, search);
        console.log('search', e.target.value)
    }

    const searchFocused = () => {
        let isFocus = !searchFocus;
        setSearchFocus(isFocus);
    };

    return (
        <div className={styles.container}>
            <section className={styles.content}>
                <Search
                    search={search}
                    searchFocus={searchFocus}
                    searchFocused={searchFocused}
                    searchResult={searchResult}
                    handlerSearch={handlerSearch} />
            </section>
        </div>
    );
};

export default App;
