        <>
            <div className={styles.search}>
                <div
                    className={backdropClasses.join(' ')}>
                </div>
                <h1 className={styles['search-title']}>
                    Simetrik Search
                </h1>
                <Input
                    type="text"
                    value={props.search}
                    onChange={props.handlerSearch}
                    onFocus={searchFocused}
                    onBlur={searchFocused}
                    placeholder="Search Data"
                    hasanimation="true" />
